const CustomImage = (props) => {
    let [ID, setID] = useState()
    let [ftype, setFtype] = useState()
    let [name, setName] = useState()
    let [path, setPath] = useState()

    console.log(props)

    useEffect(() => {
        setID(props.item.id)
        setFtype(props.item.ftype)
        setName(props.item.name)
        setPath(props.item.path)
    });

    if (ftype == 1) {
        return (
            <div
                style={{
                    width: '180px',
                    height: '180px',
                }}
            >
                
                <div
                    style={{
                        width: '180px',
                        height: '180px',
                    }}
                    onClick={() => { 
                        if (ftype === 0) {
                            props.changeImage(ID, path) 
                        } else {
                            props.changeFolder(ID)
                        }
                    }}
                >
                    <div
                        style={{
                            position: 'relative'
                        }}
                    >
                        <img
                            style={{
                                width: '180px',
                                height: '180px',
                                borderRadius: '5px',
                            }}
                            src="data:image/png;base64,"
                        />
                        <div
                            style={{
                                position: 'absolute',
                                top: '0px'
                            }}
                        >{name}</div>
                    </div>
                </div>
            </div>
        )
    }

    let src = HOST + "/dynamic/180x180" + props.item.path + props.item.name;

    console.log(props)
    console.log(src)

    return (
        <div
            style={{
                width: '180px',
                height: '180px',
                borderRadius: '5px'
            }}
        >
            <img
                style={{
                    width: '180px',
                    height: '180px',
                    borderRadius: '5px'
                }}
                onClick={() => { 
                    if (ftype === 0) {
                        props.changeImage(ID, path) 
                    } else {
                        props.changeFolder(ID)
                    }
                }}
                src={src}
                onError={({ currentTarget }) => {
                    currentTarget.onerror = null; // prevents looping
                    currentTarget.src="data:image/png;base64,";
                }}
                onerror="this.src='data:image/png;base64,'"
            />
        </div>
    )
}

const ImageList = (props) => {
    return (
        <>
            {props.items.map((item, i) => { 
                return (<CustomImage 
                    changeFolder={props.changeFolder} 
                    changeImage={props.changeImage} 
                    item={item} 
                />) 
            })}
        </>
    )
}

