const CreateFolder = (props) => {
    const [name, setName] = useState()

    return (
        <>
            <Popconfirm
                title="Имя новой директории"
                description={
                    <Input onChange={(e) => {
                        setName(e.target.value)
                    }} />
                }
                onConfirm={(e) => {
                    ImageDataService.createFolder({
                        parent_id: props.idParent,
                        name: name
                    })
                    .then(response => {
                        props.updateFunc()
                    })
                    .catch(e => {
                    });

                    console.log("test")
                    console.log(name)
                }}
                okText="Yes"
                cancelText="No"
            >
            <Button
                type="primary" icon={<FolderAddOutlined />} size='large' 
            />
            </Popconfirm>
        </>
    )
}

const ImageModal = (props) => {
    const [isLoading, setIsLoading] = useState(true)
    const [items, setItems] = useState([])
    const [total, setTotal] = useState(0)
    const [idParent, setIdParent] = useState(0)
    const [pathParent, setPathParent] = useState([])
    const [page, setPage] = useState(0)
    const [fileList, setFileList] = useState([])

    FileListManager.set(fileList, setFileList)

    console.log(fileList)

    const changeFolder = (id) => {
        console.log(id)
        setIdParent(id)
        setPage(0)
        console.log(idParent)
        pathParent.push(idParent)
        setPathParent(pathParent)
    }

    const update = () => {
        ImageDataService.list({
            parent_id: idParent,
            page: page
        })
        .then(response => {
            for (let i = 0; i < response.data.List.length; i++) {
                response.data.List[i].key = response.data.List[i].id
            }

            setItems(response.data.List)
            setTotal(response.data.Total)
        })
        .catch(e => {
        }); 
    }

    const openChange = () => {
        if (props.imageOpen) {
            update()
        }
    }

    useEffect(() => {
        if (props.imageOpen) {
            update() 
        }
    }, [idParent, page]);

    FileListManager.setUpdateFunc(update)

    return (
        <Modal
            title="Менеджер изображений"
            style={{
                top: 20,
                minWidth: 800,
            }}
            open={props.imageOpen}
            afterOpenChange={openChange}
            //onOk={() => props.setImageOpen(false)}
            onCancel={() => props.setImageOpen(false)}
            width={800}
            footer={(_, { OkBtn, CancelBtn }) => (
                <></>
            )}
        >
            <Divider />

            <Flex gap="small" wrap="wrap">
                <Button 
                    type="primary" 
                    icon={<UpCircleOutlined />} 
                    size='large'
                    onClick={() => {
                        if (pathParent.length == 0) {
                            return
                        }

                        let last = pathParent.pop()
                        setIdParent(last)
                        console.log("LAST", last)
                        console.log(pathParent)
                    }}
                />
                <Button type="primary" icon={<SyncOutlined />} size='large' />
                <Upload 
                    multiple={true}
                    showUploadList={false}
                    onChange={(e) => {
                        //console.log(e)
                    }}
                    customRequest={(e) => {
                        UploadFile(e, setFileList, idParent)
                    }}
                    {...props}
                >
                    <Button type="primary" icon={<UploadOutlined />} size='large' />
                </Upload>

                <CreateFolder 
                    updateFunc={update}
                    idParent={idParent}
                />

                <Button type="primary" icon={<DeleteOutlined />} size='large' />
                <Button type="primary" icon={<DragOutlined />} size='large' />
                <Search
                    placeholder="input search text"
                    allowClear
                    enterButton="Search"
                    size="large"
                    style={{
                        marginLeft: 'auto',
                        width: 'inherit',
                        flexShrink: 0,
                        flexGrow: 1,
                    }}
                />
            </Flex>

            <Divider />

            <Upload
                action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
                listType="picture"
                fileList={fileList}
                className="upload-list-inline"
                onRemove={(e) => {
                    FileListManager.delete(e.uid)

                    setFileList((prevFileList) => {
                        return prevFileList.filter(function(item) {
                            return item.uid !== e.uid
                        })
                    })
                }}
            />

            <Divider />

            <div
                style={{
                    display: 'flex',
                    columnGap: '10px',
                    rowGap: '10px',
                    flexWrap: 'wrap',
                }}
            >

            <ImageList items={items} 
                changeFolder={changeFolder} 
                changeImage={props.changeImage}
            />

            </div>

            <Divider />

            <Pagination 
                hideOnSinglePage={true}
                defaultCurrent={1} 
                total={total} 
                defaultPageSize={8} 
                onChange={(page) => {
                    setPage(page-1)
                }}
                current={page+1}
            />
        </Modal>
    )
}

export default ImageModal;